mapper.select:
select
project_name,project_type,inv_totbalance,rate,payback_time,payback_way,inv_balance
from t_inv_project

	
		project_type=#{invProject.projectType}
	


	
		and rate >= #{invProject.maxRate}
	


	
		and rate <=
		#{invProject.minRate}
	


	
		and payback_time >= #{invProject.minMonth}
	


	
		and payback_time <=
		#{invProject.maxMonth}
	


	
		payback_way=#{invProject.paybackWay}
	



	and project_status=40
