mapper.select:
select 
from t_loan_application
where result = 0
 and real_name = #{realName}
 and contact_number = #{contactNumber}

mapper.insert:

insert into t_loan_application (id, user_id, real_name, 
  contact_number, loan_balance, loan_term, 
  address, result, appliy_date, 
  handler_id, handler_name, handler_date
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{realName,jdbcType=VARCHAR}, 
  #{contactNumber,jdbcType=VARCHAR}, #{loanBalance,jdbcType=INTEGER}, #{loanTerm,jdbcType=INTEGER}, 
  #{address,jdbcType=VARCHAR}, #{result,jdbcType=INTEGER}, #{appliyDate,jdbcType=TIMESTAMP}, 
  #{handlerId,jdbcType=INTEGER}, #{handlerName,jdbcType=VARCHAR}, #{handlerDate,jdbcType=TIMESTAMP}
  )

insert into t_loan_application (user_id, real_name, 
  contact_number, loan_balance, loan_term, 
  address, appliy_date
  
  )
values (#{userId,jdbcType=INTEGER}, #{realName,jdbcType=VARCHAR}, 
